import { useState,useEffect } from "react";
import { Form } from "react-bootstrap";
import { Card } from "react-bootstrap";
import { propTypes } from "react-bootstrap/esm/Image";

export default function EndpointsCheckBox({searchLabel, setLinkedConceptsCallBack}){

    const [endpoints, setEndpoints] = useState({
        gemet:{
            name:'GEMET',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gemet',
            endpointChecked :false,
            hierarchicalLinksEnabled:false,
            neighborhoodLinksEnabled : false
        },gcmd:{
            name:'GCMD',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gcmd',
            endpointChecked :false,
            hierarchicalLinksEnabled: false,
            neighborhoodLinksEnabled : false
        }
    });

    const [linkedConcepts,setLinkedConcepts]=useState({
        gemet:{
            conceptLabels:[],
            conceptUris:[],
            propertyUris:[],
        },
        gcmd:{
            conceptLabels:[],
            conceptUris:[],
            propertyUris:[],
        }
    })


    const checkableProps = ["hierarchicalLinksEnabled","neighborhoodLinksEnabled"];

    function getSkosPropNames(propCategory){
        switch(propCategory){
            case 'hierarchicalLinksEnabled' : return ['broader','narrower'];
            case 'neighborhoodLinksEnabled' : return ['closeMatch'];
            default :return '';
        }
    }

    async function  fetchLinkedConcepts ( endpointKey,propCategory)  {
        try { 

            const queryTxt = 
                'PREFIX skos: <http://www.w3.org/2004/02/skos/core#> '+
                'SELECT DISTINCT ?linkedPropertyUri ?linkedConceptUri ?linkedConceptLabel  '+
                'WHERE { '+
                ' ?searchConcept skos:prefLabel ?searchLabel. '+
                ' ?searchConcept ?linkedPropertyUri ?linkedConceptUri. '+
                ' ?linkedConceptUri skos:prefLabel ?linkedConceptLabel. '+
                '  FILTER ('+ 
                '    regex(?searchLabel,"^'+searchLabel+'$","i") '+
                '  )'+
                '  FILTER ('+ 
                '    lang(?searchLabel)="en"'+
                '  )'+
                '}';

            console.log('queryTxt',queryTxt);


            const endpoint_uri = endpoints[endpointKey]['uri'];

            const response = await fetch(endpoint_uri,{
                method: "POST",
                credentials: "same-origin",
                headers:{
                    "Content-Type": 'application/x-www-form-urlencoded',
                    "xhrFields": {withCredentials: true},
                     "Accept" : "application/json",
                },
                redirect: "follow",
                referrerPolicy: "no-referrer",
                body: "query="+ queryTxt
            }
            )            

            const jsonData = await response.json();

            const skosPropNames = getSkosPropNames(propCategory);

            const retrievedLinkedConceptUris =[];
            const retrievedLinkedConceptLabels =[];
            const retrievedLinkedPropertyUris =[]; 

            if(jsonData){
                if(jsonData.results){
                    if(jsonData.results.bindings){

                        jsonData.results.bindings.map( binding => {


                            retrievedLinkedConceptUris.push(binding['linkedConceptUri']['value']);
                            retrievedLinkedConceptLabels.push(binding['linkedConceptLabel']['value']);
                            retrievedLinkedPropertyUris.push(binding['linkedPropertyUri']['value']);
                        });


                        // console.log('jsonData.results.bindings',jsonData.results.bindings);
                    }
                }
            }

            const updatedLinkedConcepts = {
                ...linkedConcepts,
                [endpointKey]:{
                    conceptLabels:retrievedLinkedConceptLabels,
                    conceptUris:retrievedLinkedConceptUris,
                    propertyUris:retrievedLinkedPropertyUris,
                }
            };
            setLinkedConcepts(updatedLinkedConcepts);
            setLinkedConceptsCallBack(updatedLinkedConcepts);

            console.log("fetchLinkedConcepts for: "+endpointKey);
            console.log(retrievedLinkedConceptLabels);
            console.log(retrievedLinkedConceptUris);
            console.log(retrievedLinkedPropertyUris);


        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    function handleSwitchClick(endpointKey,propCategory,checked){
        console.log('endpointKey',endpointKey);
        console.log('\t enabled',checked);
        const updatedEndpoints = {
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                [propCategory]: checked}
        };
        setEndpoints(updatedEndpoints);

        console.log("updated endpoint "+propCategory,updatedEndpoints);

        if(searchLabel !=='' && checked)
            fetchLinkedConcepts(endpointKey,propCategory)
    }

    function handleEndpointClick(endpointKey,checked){
        console.log('handleEndpointClick: ',endpointKey);
        console.log('\t checked',checked);
        const updatedEndpoints = {
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                ['endpointChecked']: checked}
        };
        setEndpoints(updatedEndpoints);

        if(!checked){
            const updatedLinkedConcepts = {
                ...linkedConcepts,
                [endpointKey]:{
                    labels:[],
                    uris:[]
                }
            };
            setLinkedConcepts(updatedLinkedConcepts);
            setLinkedConceptsCallBack(updatedLinkedConcepts);
        }else{

        }

        console.log("updated endpoints ",updatedEndpoints);

    }


    return(
        <Card>
            <Card.Header>
                Enable open link discovery
            </Card.Header>
            <Card.Body>
                <Form>
                    { Object.entries(endpoints).map( ([endpointKey, endpointProps]) =>
                    <>

                        <Form.Check 
                            type="checkbox"
                            variant="secondary"
                            id={`${endpointKey}-${endpointProps.name}-checkbox`}
                            label={endpointProps.name}
                            onChange={e => handleEndpointClick(endpointKey,e.target.checked)}
                        />

                        {(endpointProps['endpointChecked'])?(
                                checkableProps.map( propName =>
                                <>
                                    <Form.Check
                                        type="switch"
                                        id={`${endpointKey}-${propName}-switch`}
                                        label={propName.replace('Enabled','').replace("L",' l')}
                                        onChange={e => handleSwitchClick(endpointKey,propName,e.target.checked)}
                                    />
                                    {   (searchLabel!=='')?(
                                            <p>{linkedConcepts[endpointKey]['conceptLabels'].map(l => l+" ")}</p>
                                        ):(
                                            <> </>
                                        )
                                    }
                                </>
                                )
                        ):(
                            <> </>
                        )
                        }
                    </>
                    )}
                </Form>
            </Card.Body>

        </Card>
    )
}
