import {useState,useEffect} from 'react'
import Table from 'react-bootstrap/Table';

function BindingVarTable(){

    const endpoint_uri = 'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph';
    const query_txt = 'select * where {?s ?p ?o} limit 10';


    const [jsonResponse,setJsonResponse] = useState({});
    const [varNames,setVarNames] = useState([]);

    async function  fetchData (url, d )  {
        try {   

            const response = await fetch(url,{
                method: "POST",
                credentials: "same-origin",
                headers:{
                    "Content-Type": 'application/x-www-form-urlencoded',
                    "xhrFields": {withCredentials: true},
                     "Accept" : "application/json",
                },
                redirect: "follow",
                referrerPolicy: "no-referrer",
                body: "query="+d
            }
            )            


            const jsonData = await response.json();
            setJsonResponse(jsonData);


            console.log('jsonResponse:', jsonResponse);

            if(jsonData){
                if(jsonData.head){
                    if(jsonData.head.vars){
                        setVarNames(jsonData.head.vars);
                    }
                }
            }

            console.log('varNames:', varNames); 

            return new Promise ((resolve,reject) =>{
                if(jsonData){
                    resolve();
                }else{
                    reject(new Error('Empty answer'));
                }

            })


        } catch (error) {
            console.log('Error:', error);
            reject(new Error('Empty answer'));
        }
    };

    useEffect (
        () =>{
            const resp =  fetchData(endpoint_uri, query_txt);
        },
        []
    );

    return(
        <div>
            {varNames ?(
                <Table striped bordered hover size='sm'>
                    <thead>
                        <tr>
                            <th>{varNames.map(v => v )}</th>
                        </tr>
                    </thead>
                    <tbody>
                    {/* <tr>
                        <td>{jsonResponse.results.bindings.map(tuple =>  {
                            varNames.map(v => tuple[v].value)
                        })}</td>
                    </tr> */}
                    <tr>
                        {
                        varNames.map(varName =>{
                            <td>{jsonResponse.results.bindings.map(tuple =>  
                                 tuple[varName].value
                            )}</td>
                        })}
                    </tr>
                    </tbody>

                </Table>
            ):(
                <p> Data not yet available ... </p>
            )}
        </div>
    );
}
export default BindingVarTable;
