import React, {useEffect, useState} from 'react';

const FetchUcmmtKg = () => {
    const [data, setData] = useState(null);

    useEffect(() => {
        fetchData(
            'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph',
            'query=select * where {?s ?p ?o} limit 10'
        );
    }, []);

    const fetchData = async (url, data ) => {
        try {

            const response = await fetch(url,{
                method: "POST",
                // mode: "no-cors",
                credentials: "same-origin",
                headers:{
                    "Content-Type": 'application/x-www-form-urlencoded',
                    "xhrFields": {withCredentials: true},
                    // "Accept" : "application/json",
                },
                redirect: "follow",
                referrerPolicy: "no-referrer",
                body: data
            }
            );
            console.log('response:', response);
            console.log('response status:',response.formData)

            const textData = await response.text();
            setData(textData)


        } catch (error) {
            console.log('Error:', error);
        }
    };

    return (
        <div>
            {data ? (

                <ul>
                {data.map(triple => <ui> {triple} </ui>)}
                </ul>
                // <table>
                //     <tr>
                //         <th>Id</th>
                //         <th>Name</th>
                //         <th>Email</th>
                //         <th>Phone</th>
                //         <th>Website</th>
                //     </tr>
                //     {data.map((item) => (

                //         <tr key={item.id}>

                //             <td>{item.id}</td>
                //             <td>{item.name}</td>
                //             <td>{item.email}</td>
                //             <td>{item.phone}</td>
                //             <td>{item.website}</td>

                //         </tr>

                //     ))}
                // </table>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default FetchUcmmtKg;
