import {useState,useEffect} from 'react'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

export default function SearchLabelForm(){

    const [searchLabel, setSearchLabel] = useState(''); 

    const [storedLabels,setStoredLabels] = useState([]);

    const [dataLoaded, setDataLoaded] = useState(false);

    async function  fetchStoredLabels ( )  {
        try { 

            if(dataLoaded)
                return;

            const queryTxt = 
                'PREFIX skos: <http://www.w3.org/2004/02/skos/core#> '
                +'SELECT DISTINCT ?storedLabel WHERE {?concept skos:prefLabel ?storedLabel } limit'

            const endpoint_uri = 'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph'

            const response = await fetch(endpoint_uri,{
                method: "POST",
                credentials: "same-origin",
                headers:{
                    "Content-Type": 'application/x-www-form-urlencoded',
                    "xhrFields": {withCredentials: true},
                     "Accept" : "application/json",
                },
                redirect: "follow",
                referrerPolicy: "no-referrer",
                body: "query="+ queryTxt.replaceAll('\n',' ')
            }
            )            

            const jsonData = await response.json();

            console.log('json storedLabels:', jsonData);

            if(jsonData){
                if(jsonData.results){
                    if(jsonData.results.bindings){

                        setDataLoaded(true);
                        setStoredLabels(jsonData.results.bindings);

                        console.log('jsonData.results.bindings',jsonData.results.bindings);
                    }
                }

            }

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }


    function handleSearchClick(){

        console.log('click');
    }


    useEffect (
        () =>{
            fetchStoredLabels();
       },[dataLoaded] 
   );


    return (
        dataLoaded?(
            <InputGroup className="mb-3">
                    <InputGroup.Text id='searchLabelInputGroup'>{"Label :"}</InputGroup.Text>
                    <Form.Control 

                        placeholder='Type a label'
                        aria-aria-label='search label'
                        aria-describedby='searchLabelInputGroup'

                        // autoComplete='on'
                        // data = {['un','deux','trois']}
                        list='storeLabelListOption'
                        value={searchLabel} 
                        onChange={e =>setSearchLabel(e.target.value)}
                    />
                    <datalist id = 'storeLabelListOption'>
                        {
                        storedLabels.map( binding => 
                            <option >{binding.storedLabel.value}</option>
                        )}
                    </datalist>


                    <Button variant="outline-secondary" id="button-search" onClick={handleSearchClick}>
                        search
                    </Button> 
            </InputGroup>
        ):(
            <p>loading ... </p>
        )
    )

}
