import React, {useState,useEffect} from 'react'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';

export default function SearchLabelForm(){

    // const [searchLabel, setSearchLabel] = useState(''); 

    const [storedLabels,setStoreLabels] = useState([]);

    useEffect (
         () =>{
              fetchStoredLabels();
        },[]

    );

    function handleSearchClick(){
        ;
    }

    async function  fetchStoredLabels ( )  {
        try { 
            const queryTxt = 
                'PREFIX skos: <http://www.w3.org/2004/02/skos/core#> '
                +'SELECT DISTINCT ?searchLabel WHERE {?concept skos:prefLabel ?searchLabel }'

            const endpoint_uri = 'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph'

            const response = await fetch(endpoint_uri,{
                method: "POST",
                credentials: "same-origin",
                headers:{
                    "Content-Type": 'application/x-www-form-urlencoded',
                    "xhrFields": {withCredentials: true},
                     "Accept" : "application/json",
                },
                redirect: "follow",
                referrerPolicy: "no-referrer",
                body: "query="+ queryTxt.replaceAll('\n',' ')
            }
            )            

            const jsonData = await response.json();

            console.log('json storedLabels:', jsonData);

            if(jsonData){
                if(jsonData.results){
                    if(jsonData.results.bindings){
                        retrievedLabels = [];
                        jsonData.results.bindings.map(
                            binding => {
                                retrievedLabels.push(binding['searchLabel']['value']);
                            }
                        );
                        setStoreLabels(retrievedLabels);
                        console.log('retrievedLabels',retrievedLabels);
                    }
                }

            }

        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }




    return(
        <div>
            {storedLabels?(
            <InputGroup className="mb-3">
                    <InputGroup.Text id='searchLabelInputGroup'>{"Explore datasets mentioning : "}</InputGroup.Text>
                    <input 
                        class="form-control"
                        placeholder='Type a label'
                        aria-aria-label='search label'
                        aria-describedby='searchLabelInputGroup'

                        autoComplete='on'
                        list='storeLabelListOption'
                        // value={searchLabel} 
                        // onChange={e =>setSearchLabel(e.target.value)}
                    />
                    <datalist id = 'storeLabelListOption'>
                        {storedLabels.map( l => {
                            <option >{l}</option>
                        })}
                        {/* <option >un</option>
                        <option >deux</option>
                        <option >trois</option> */}
                    </datalist>


                    <Button variant="outline-secondary" id="button-search" onClick={handleSearchClick}>
                        search
                    </Button> 
            </InputGroup>
            ):(
                <p> Loading ... </p>
            )}
        </div>
    )
}
