        //         const triples = res.data;
        //         this.setState({triples});
        //     }
        // )
    }

    render(){
        return(
        <div>
            {this.state.triples ? (

                <ul>
                    {this.state.triples.map(triple => <ui> {triple} </ui>)}
                </ul>

            ) : (
                <p>Loading...</p>
            )}
        </div>
        )
    }


}
