import {useState} from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'

import SearchLabelForm from './components/SearchLabelForm';
import RetrievedDatasetVarTable from './components/bindingVarTable/RetrievedDatasetVarTable'



const App = () => {

  const [searchLabel, setSearchLabel] = useState(''); 

  const kb_uri = 'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph';


  return (
    <Container>
      <Row>
        <h1> Discovery of Datasets from search label </h1>
      </Row>
      <Row>
        <Col xs={4}>
         <SearchLabelForm updateCallBack={setSearchLabel} /> 
         <p>{'selected label: '+searchLabel}</p>
        </Col>
        <Col xs={8}>
          <RetrievedDatasetVarTable selectedLabel={searchLabel} endpoint_uri={kb_uri} />
        </Col>
      </Row>
    </Container>
  );
};

export default App;

