import React from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'

import SearchLabelForm from './components/SearchLabelForm';
import ExecQueryBindingVarTable from './components/bindingVarTable/ExecQueryBindingVarTable';




const App = () => {

  const [searchLabel, setSearchLabel] = useState(''); 

  return (
    <Container fluid="md">
      <Row>
        <Col>
         <SearchLabelForm sendSearchLabel={setSearchLabel} /> 
         <p>{'selected label: '+searchLabel}</p>
        </Col>
        <Col>
          <ExecQueryBindingVarTable />
        </Col>
      </Row>
    </Container>
  );
};

export default App;

