import { useState,useEffect } from "react";
import { Form } from "react-bootstrap";
import { Card } from "react-bootstrap";

export default function EndpointsCheckBox({updateEndpointsCallBack}){

    const [endpoints, setEndpoints] = useState({
        gemet:{
            name:'GEMET',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gemet',
            endpointChecked :false,
            hierarchicalLinksEnabled:false,
            neighborhoodLinksEnabled : false},

        gcmd:{
            name:'GCMD',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gcmd',
            endpointChecked :false,
            hierarchicalLinksEnabled: false,
            neighborhoodLinksEnabled : false},
        }

    );

    const checkableProps = ["hierarchicalLinksEnabled","neighborhoodLinksEnabled"];

    function handleSwitchClick(endpointKey,propName,checked){
        console.log('endpointKey',endpointKey);
        console.log('\t enabled',checked);
        const updatedEndpoints = {
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                [propName]: checked}
        };
        setEndpoints(updatedEndpoints);
        updateEndpointsCallBack(updatedEndpoints);

        console.log("updated endpoint "+propName,updatedEndpoints);
    }

    function handleEndpointClick(endpointKey,propName,checked){
        console.log('endpointKey',endpointKey);
        console.log('\t checked',checked);
        const updatedEndpoints = {
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                [propName]: checked}
        };
        setEndpoints(updatedEndpoints);
        updateEndpointsCallBack(updatedEndpoints);

        console.log("updated endpoints "+propName,updatedEndpoints);
    }

    return(
        <Card>
            <Card.Header>
                Enable open link discovery
            </Card.Header>
            <Card.Body>
                <Form>
                    { Object.entries(endpoints).map( ([endpointKey, endpointProps]) =>
                    <>

                        <Form.Check 
                            type="checkbox"
                            id={`${endpointKey}-${endpointProps.name}-checkbox`}
                            label={endpointProps.name}
                            onChange={e => handleEndpointClick(endpointKey,'endpointChecked',e.target.checked)}
                        />
                        {(endpointProps['endpointChecked'])?(
                                checkableProps.map( propName =>
                                <Form.Check 
                                    type="switch"
                                    id={`${endpointKey}-${propName}-switch`}
                                    label={propName.replace('Enabled','').replace("L",' l')}
                                    onChange={e => handleSwitchClick(endpointKey,propName,e.target.checked)}
                                />)
                        ):(
                            <> </>
                        )
                        }
                    </>
                    )}
                </Form>
            </Card.Body>

        </Card>
    )
}
