import { useState,useEffect } from "react";
import { Form } from "react-bootstrap";
import { Card } from "react-bootstrap";

export default function EndpointsCheckBox({updateEndpointsCallBack}){

    const [endpoints, setEndpoints] = useState({
        gemet:{
            offset:0,
            name:'GEMET',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gemet',
            enabled:false,
            searchHierarchicalLinks:false,
            searchNeibourhoodLinks : false},
        gcmd:{
            offset:1,
            name:'GCMD',
            uri:'http://13.38.36.148:8080/rdf4j-server/repositories/gcmd',
            enabled:false,
            hierarchicalLinksEnabled: false,
            neibourhoodLinksEnabled : false},
        }

    );

    const checkableProps = ["hierarchicalLinksEnabled","neibourhoodLinksEnabled"];

    function handleSwitchClick(endpointKey,propName,checked){
        console.log('endpointKey',endpointKey);
        console.log('\t checked',checked);
        setEndpoints({
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                [propName]: checked}
        });

        console.log("copy of endpoints",{
            ...endpoints,
            [endpointKey] : { 
                ...endpoints[endpointKey],
                [propName]: checked}
        });
    }

    return(
        <Card>
            <Card.Header>
                Open search with
            </Card.Header>
            <Card.Body>
                <Form>
                    { Object.entries(endpoints).map( ([endpointKey, endpointProps]) =>
                    <>
                        <p>{endpointKey}</p>
                        { checkableProps.maps( (propName) =>
                        <Form.Check 
                            type="switch"
                            id={`${propName}-switch`}
                            label={propName.replace('Enabled','')}
                            onChange={e => handleSwitchClick(endpointKey,propName,e.target.checked)}

                        />
                        )}
                    </>
                    )}
                </Form>
            </Card.Body>

        </Card>
    )
}
