import {useState} from 'react';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col'

import SearchLabelForm from './components/SearchLabelForm';
import RetrievedDatasetVarTable from './components/bindingVarTable/RetrievedDatasetVarTable'
import EndpointsCheckBox from './components/EndpointsCheckBox';
import LinkedSearchDatasetVarTable from './components/bindingVarTable/LinkedSearchDatasetVarTable';


const App = () => {

  const [searchLabel, setSearchLabel] = useState(''); 

  const kb_uri = 'http://13.38.36.148:8080/rdf4j-server/repositories/ucmm_instance_graph';

  const [linkedConceptMap, setLinkedConceptMap] = useState({});

  return (
    <Container>
      <Row>
        <div class="d-flex justify-content-center">
          <h1 > Discovery of datasets mentioning a search label </h1>
        </div>
      </Row>
      <Row>
        <Col lg={4} className="d-grid gap-2">
         <SearchLabelForm updateCallBack={setSearchLabel} />
         <EndpointsCheckBox
          searchLabel={searchLabel}
          setLinkedConceptMapCallBack={setLinkedConceptMap}
          />
        </Col>
        <Col lg={8}>
          <LinkedSearchDatasetVarTable linkedConceptMap={linkedConceptMap} ></LinkedSearchDatasetVarTable>
          <RetrievedDatasetVarTable extentedSearchPath={[]} selectedLabel={searchLabel} endpoint_uri={kb_uri} />
        </Col>
      </Row>
    </Container>
  );
};

export default App;

